//checkbox sanitization function
if ( ! function_exists( 'minnak_sanitize_checkbox' ) ) {
	/**
			* Switch sanitization
			*
			* @param string $input Switch value.
			* @return integer  Sanitized value
			*/
	function minnak_sanitize_checkbox( $input ) {
		if ( false === $input ) {
				return 0;
		} else {
				return 1;
		}
	}
}

//select sanitization function
function minnak_sanitize_select( $input, $setting ) {
	//input must be a slug: lowercase alphanumeric characters, dashes and underscores are allowed only
	$input = sanitize_key( $input );
	//get the list of possible select options
	$choices = $setting->manager->get_control( $setting->id )->choices;
	//return input if valid or return default option
	return ( array_key_exists( $input, $choices ) ? $input : $setting->default );
}

function minnak_social_links_array() {
	$social_sites = array( 'Twitter', 'Facebook', 'Instagram', 'Reddit', 'Digg', 'AIM', 'Pinterest', 'Dribbble', 'Behance', 'Tumblr', 'Flickr', 'Linkedin', 'Youtube', 'Vimeo', 'Discord', 'Whatsapp', 'Codepen', 'Spotify', 'Anchor-Podcast', 'Google-Podcast', 'Apple-Podcast', 'RSS' );
	return $social_sites;
}

add_action( 'customize_register', 'minnak_social_links_customizer' );
function minnak_social_links_customizer( $wp_customize ) {
	$wp_customize->add_section(
		'minnak_social_links',
		array(
			'title'       => __( 'Social media links', 'minnak' ),
			'priority'    => 135,
			'description' => __( 'Enter the full URL for each service to appear in the sidebar.', 'minnak' ),
		)
	);
	$social_sites = minnak_social_links_array();
	$priority = 5;
	foreach ( $social_sites as $social_site ) {
		$wp_customize->add_setting(
			"$social_site",
			array(
				'type'              => 'theme_mod',
				'capability'        => 'edit_theme_options',
				'sanitize_callback' => 'esc_url_raw',
			)
		);
		$wp_customize->add_control(
			$social_site,
			array(
				'label'    => $social_site . ' ' . __( 'URL:', 'minnak' ),
				'section'  => 'minnak_social_links',
				'type'     => 'text',
				'priority' => $priority,
			)
		);
		$priority = $priority + 1;
	}
}

